import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import { register } from '../../Actions/Registeraction';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { signin } from '../../Actions/Signinaction';
import { useDispatch, useSelector } from 'react-redux';










function CarbonScreen (props) {

    const dispatch = useDispatch();
    /*setting state to store selected date*/ 
    const [dateSelected, setDateSelected] = useState('');

    /* Getting state of the carbon intensity from the redux store*/
    const carbonIntensityData = useSelector(state => state.userSignin);
    const { carbonData } = carbonIntensityData;

    /*getting today date*/
    const date = new Date();
    let day = ("0" + date.getDate()).slice(-2);
    let month = ("0" + (date.getMonth() + 1
    )).slice(-2);
    let year = date.getFullYear();
    let today = (year + "-" + month + "-" + day)

    /*getting the date of the last first day*/
    let lastFirstDay = new Date(date)
    lastFirstDay.setDate(lastFirstDay.getDate() - 1)

    /*getting the date of the last second day*/
    let lastSecondDay = new Date(date)
    lastSecondDay.setDate(lastSecondDay.getDate() - 2)

    /*getting the date of the last third day*/
    let lastThirdDay = new Date(date)
    lastThirdDay.setDate(lastThirdDay.getDate() - 3)

    /*getting the date of the last fourth day*/
    let lastFourthDay = new Date(date)
    lastFourthDay.setDate(lastFourthDay.getDate() - 4)

    /*getting the date of the last fifth day*/
    let lastFifthDay = new Date(date)
    lastFifthDay.setDate(lastFifthDay.getDate() - 5)


    /*dispatching action for fetching carbon intensity data for selected date*/
    getDataHandler = (event) => {
        setDateSelected(event)
        dispatch(signin(dateSelected));
        console.log ('hello')
    }

    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='content content-margined'>
                   

                  

                        <div className='product-list'>

                        <table className='table'>
                            <thead>
                                <tr>

                                <th> SELECT A DATE </th>
                               
                                                                
                                </tr>
                            </thead>
                            
                            <tbody>
                                
                                <tr>
                                    <td><button className='button primary' onClick={this.getDataHandler()}> {lastFirstDay.toDateString()} </button> </td>
                                        
                                   </tr>

                                <tr>
                                    <td> <button className='button primary'> {lastSecondDay.toDateString()} </button> </td>

                                </tr>

                                <tr>
                                    <td> <button className='button primary'> {lastThirdDay.toDateString()} </button> </td>
                                </tr>

                                <tr>
                                    <td> <button className='button primary'> {lastFourthDay.toDateString()} </button> </td>
                                </tr>

                                <tr>
                                    <td> <button className='button primary'> {lastFifthDay.toDateString()} </button> </td>

                                </tr>
                                
                                
                            </tbody>
                        </table>
                    </div>

                




                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default CarbonScreen;
