import { USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_REGISTER_FAIL } from '../Constants/Registerconstants';


function userRegisterReducer(state = { registerInfo:'true'}, action) {
    switch (action.type) {
        case USER_REGISTER_REQUEST:
            return { loading: true, email: true };
        case USER_REGISTER_SUCCESS:

            return { loading: false, registerInfo: action.payload };
        case USER_REGISTER_FAIL:
            return { loading: false, error: action.payload };

        default: return state;
    }



}
export { userRegisterReducer };
