import { GET_DATA_ACTION_REQUEST, GET_DATA_ACTION_SUCCESS, GET_DATA_ACTION_FAIL } from '../Constants/Signinconstants';


function userSigninReducer(state = {carbonData : ''}, action) {
    switch (action.type) {
        case GET_DATA_ACTION_REQUEST:
            return { loading: true };
        case GET_DATA_ACTION_SUCCESS:

            return { loading: false, carbonData : action.payload };
        case GET_DATA_ACTION_FAIL:
            return { loading: false, error: action.payload };
        

         default : return state;
    }

    

}
export { userSigninReducer };
