import ReactDOM from 'react-dom';
import React, { Component, useReducer } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import countryList from 'react-select-country-list';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';

import { useDispatch, useSelector } from 'react-redux';
import { savePayment } from '../../Actions/Cartaction';
import Checkoutsteps from '../Checkoutsteps/Checkoutsteps';










function Paymentpage(props) {

    const dispatch = useDispatch();
    const [paymentmethod, setPaymentmethod] = useState('');
    const countries = countryList().getData();
   


    const submitHandler = (e) => {
        e.preventDefault();

        dispatch(savePayment(paymentmethod));
        props.history.push('placeorder');
        
    }

    



    return (
       
        <div className='Frontpage grid-container'>
            <div>
                <Checkoutsteps step1 step2  step3>  </Checkoutsteps>
            </div>


            <main className='main'>
                <div className='form'>

                    <form onSubmit={submitHandler}>

                        <ul className='form-container'>
                            <li>
                                <h2> Payment </h2>
                            </li>
                           
                            <li>
                                <div>
                                <input required='true' type='radio' name='paymentmethod' id='paymentmethod' value='paypal' onChange={(event) => { setPaymentmethod(event.target.value) }} />
                                <label htmlFor='paymentmethod'> Paypal </label>
                                </div>
                            </li>
                            <li>
                                <div>
                                <input required='true' type='radio' name='paymentmethod' id='paymentmethod' value='card' onChange={(event) => { setPaymentmethod(event.target.value) }} />
                                <label htmlFor='paymentmethod'> Card </label>
                                </div>
                            </li>
                            
                            

                            <li>
                                <button className='button primary' type='submit'> Continue </button>
                            </li>

                        </ul>


                    </form>



                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default Paymentpage;
