import Axios from "axios";
import { Cookie } from 'js-cookie';
import { USER_SIGNIN_REQUEST, USER_SIGNIN_SUCCESS, USER_SIGNIN_FAIL } from '../Constants/Signinconstants';

const signin = (email, password) => async (dispatch, getState) => {
    dispatch({ type: USER_SIGNIN_REQUEST, payload: { email, password }})

    try {
        const { data } = await Axios.post('http://localhost:3001/api/users/signin', { email, password });
        dispatch({ type: USER_SIGNIN_SUCCESS, payload: data });


        const { userInfo: { userInfo } } = getState();
        Cookie.set("userInfo", JSON.stringify(data));

    }
    catch (error) {
        dispatch({ type: USER_SIGNIN_FAIL, payload: error.message });
    }

} 

export { signin };
