import Axios from "axios";
import Cookie  from 'js-cookie';
import { USER_SIGNIN_REQUEST, USER_SIGNIN_SUCCESS, USER_SIGNIN_FAIL, REFRESH_REGISTER_INFO_STATE } from '../Constants/Signinconstants';

const signin = (dateSelected) => async (dispatch) => {
    dispatch({ type: USER_SIGNIN_REQUEST, payload: {dateSelected}})

    try {
        const { data } = await Axios.get('https://api.carbonintensity.org.uk/intensity/date/' );
        dispatch({ type: USER_SIGNIN_SUCCESS, payload: data });



        Cookie.set("userInfo", JSON.stringify(data));

    }
    catch (error) {
        dispatch({ type: USER_SIGNIN_FAIL, payload: error.message });
    }

} 

const refreshregisterinfo = () => async (dispatch) => {
    dispatch({ type: REFRESH_REGISTER_INFO_STATE, payload: true })


} 


export { signin, refreshregisterinfo };
