import { createStore, combineReducers, applyMiddleware, compose  } from 'redux';
import thunk from 'redux-thunk';
import { createLogger } from 'redux-logger'
import { getDataReducer } from './Reducers/GetDataReducer.js';
import Cookie from 'js-cookie'; /*Used Cookie in preserving the state when screen refreshed is perform */



    



const logger = createLogger();

const rootreducer = combineReducers({
    carbon: getDataReducer
   
})

const composeEnhancer = window._REDUX_DEVTOOLS_EXTENSION_COMPOSE_ || compose;
const store = createStore(rootreducer,  composeEnhancer(applyMiddleware(thunk, logger)))


export default store
