import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import { register } from '../../Actions/Registeraction';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { signin } from '../../Actions/Signinaction';
import { useDispatch, useSelector } from 'react-redux';
import { saveProduct, ListProduct, deleteProduct } from '../../Actions/Productaction';









function CarbonScreen (props) {

    const dispatch = useDispatch();
    const [indexy, setIndex] = useState('');

    /*getting today date*/
    let date = new Date();
    let day = ("0" + date.getDate()).slice(-2);
    let month = ("0" + (date.getMonth() + 1
    )).slice(-2);
    let year = date.getFullYear();
    let today = (year + "-" + month + "-" + day)

/*getting the date of the last first day*/
    let lastFirstDay = new Date(date)
    lastFirstDay.setDate(lastFirstDay.getDate() - 1)

    /*getting the date of the last second day*/
    let lastSecondDay = new Date(date)
    lastFirstDay.setDate(lastSecondDay.getDate() - 2)

    /*getting the date of the last third day*/
    let lastThirdDay = new Date(date)
    lastFirstDay.setDate(lastThirdDay.getDate() - 3)

    /*getting the date of the last fourth day*/
    let lastFourthDay = new Date(date)
    lastFirstDay.setDate(lastFourthDay.getDate() - 4)

    /*getting the date of the last fifth day*/
    let lastFifthDay = new Date(date)
    lastFirstDay.setDate(lastFifthDay.getDate() - 5)

    
    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='content content-margined'>
                   

                  

                        <div className='product-list'>

                        <table className='table'>
                            <thead>
                                <tr>

                                <th> DATE </th>
                                <th> Forecast Intensity </th>
                                <th> Actual intensity </th>
                                <th> Index </th>
                                                                
                                </tr>
                            </thead>
                            
                            <tbody>
                                
                                <tr>
                                    <td>{lastFirstDay.toDateString()} </td>
                                        <td>'Tuesday' </td>
                                        <td>'TWednesday' </td>
                                        <td>'Tuesday' </td>                                                                         
                                      
                                   </tr>

                                <tr>
                                    <td>{lastSecondDay.toDateString()} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>

                                <tr>
                                    <td>{lastThirdDay.toDateString()} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>

                                <tr>
                                    <td>{lastFourthDay.toDateString()} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>

                                <tr>
                                    <td>{lastFifthDay.toDateString()} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>
                                
                                
                            </tbody>
                        </table>
                    </div>

                




                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default CarbonScreen;
