import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import {getDataAction } from '../../Actions/GetDataAction.js';
import { useDispatch, useSelector } from 'react-redux';










function PlotScreen (props) {

    const dispatch = useDispatch();
    /*setting state to store selected date*/ 
    const [dateSelected, setDateSelected] = useState('');

    /* Getting state of the carbon intensity from the redux store*/
    const carbonIntensityData = useSelector(state => state.carbon);
    const { loading, carbonData } = carbonIntensityData
    


    useEffect(() => {
        if (dateSelected) {

            carbonData.map((data) => { console.log(data.from) })   
            

                        
        }
    }, [dateSelected]);



    /*dispatching action for fetching carbon intensity data if date is selected*/
    const getDataHandler = () => {
        
       
        carbonData.map((data) => { console.log(data.intensity.actual) })  
        
    }

    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='content content-margined'>
                   THIS IS PLOT SCREEN
          
                        
                    <button onClick={getDataHandler()}> CLICK HERE</button>

                




                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default PlotScreen;
