import { createStore, combineReducers, applyMiddleware, compose  } from 'redux';
import { productListReducer, productDetailsReducer, productsaveReducer, productDeleteReducer } from './Reducers/ProductReducer';
import thunk from 'redux-thunk';
import { createLogger } from 'redux-logger'
import { cartReducer } from './Reducers/CartReducer';
import { userSigninReducer } from './Reducers/SigninReducer';
import { userRegisterReducer } from './Reducers/RegisterReducer';

    



const initialState = { cart: { cartItems, shipping, payment }, userSignin: { userInfo } };
const logger = createLogger();
const rootreducer = combineReducers({
    userSignin: userSigninReducer, userRegisterReducer,
   
})

const composeEnhancer = window._REDUX_DEVTOOLS_EXTENSION_COMPOSE_ || compose;
const store = createStore(rootreducer, initialState, composeEnhancer(applyMiddleware(thunk, logger)))


export default store
