import { CART_ADD_ITEM, REMOVE_FROM_CART} from '../Constants/Cartconstants'

function cartReducer(state = { cartItems: [] }, action) {
    switch (action.type) {
        case CART_ADD_ITEM:
            const item = action.payload;
        /* check if new item is already existing*/
            const existingproduct  = state.cartItems.find(x => x.product === item.product);
            if (existingproduct) {
                return {
                    /* If product being added was already existing, replace the already existing product with the new one which is the item*/
                    cartItems: state.cartItems.map(x => x.product === existingproduct.product ? item : x)
                }
               
            }
            return {
                cartItems: [...state.cartItems, item]
            }
        case REMOVE_FROM_CART:
            return { cartItems: state.cartItems.filter(item => item.product !== action.payload) }

        default:
            return state
    }

}

export { cartReducer }
