import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/cartpageonly.css';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { addToCart, removeFromCart } from '../../Actions/Cartaction';
import { useDispatch, useSelector } from 'react-redux';
import Checkoutsteps from '../Checkoutsteps/Checkoutsteps';









function Placeorderpage  (props)  {

    const cart = useSelector(state => state.cart); /* access the state in the cart state inside store generated from cart reducer*/
    const { cartItems, shipping, payment } = cart; /* declaring cartitems as cart in the above step*/

    if (!shipping) {
        props.history.push('/shipping');
    }

    else if (!payment) {
        props.history.push ('/payment')
    };

    const itemsPrice = cartItems.reduce((a, c) => a + c.price * c.qty, 0);
    const shippingPrice = itemsPrice > 100 ? 0 : 10;
    const taxPrice = 0.15 * itemsPrice;
    const totalPrice = itemsPrice + shippingPrice + taxPrice;
        
    const productid = props.match.params.id;
    const qty = props.location.search ? Number(props.location.search.split("=")[1]) : 1;
    const dispatch = useDispatch();


    function placeOrderHandler() {

    }


   
    useEffect(() => {
       
    }, [])


        
        return  (
            <div className='Frontpage grid-container'>
                <div>
                    <Checkoutsteps step1 step2 step3 step4> </Checkoutsteps>
                </div>

                <main className='main'>
                    <div className='placeorder' >

                        <div className='placeorder-info'>
                            <div>
                                <h3> Shipping </h3>
                                <div>
                                    {cart.shipping.address},
                                    {cart.shipping.postalcode},
                                    {cart.shipping.city},
                                    {cart.shipping.country},

                                </div>
                                
                            </div>
                            <div>

                                <h3> Payment </h3>

                                <div>
                                    Payment Method : {cart.payment.paymentmethod}
                                </div>
                            </div>
                            <div>
                                <ul className='cart-list-container'>

                                    <li>
                                        <h3> Shopping Cart </h3>
                                        <div> Price </div>

                                    </li>

                                    {cartItems.length === 0 ?
                                        <div> Cart is empty </div> :
                                        cartItems.map(item =>
                                            <div className='cart-list-container-item'>
                                                <div className='cart-image'>
                                                    <img src={item.image} alt='product' />
                                                </div>
                                                <div className='cart-name'>
                                                    <div>
                                                        <Link className='cart-name-link' to={"/products/" + item.product}>
                                                            {item.name}
                                                        </Link>
                                                    </div>
                                                    <div>
                                                        Qty: {item.qty}
                                                        
                                                    </div>

                                                </div>

                                                <div className='cart-price'>
                                                    &#163;{item.price}

                                                </div>

                                            </div>
                                        )
                                    }

                                </ul>
                            </div>
                            

                        </div>

                        <div className='placeorder-action'>
                            <ul>
                                <li>
                                    <button onClick={placeOrderHandler}> Place Order </button>
                                </li>
                                <li>
                                    <h3> Surmmary </h3>
                                </li>

                                <li>
                                    <div> Items </div>
                                    <div> &#163; {itemsPrice} </div>
                                </li>

                                <li>
                                    <div> Shipping </div>
                                    <div> &#163; {shippingPrice} </div>
                                </li>

                                <li>
                                    <div> Tax </div>
                                    <div> &#163; {taxPrice} </div>
                                </li>

                                <li>
                                    <div> Order Total </div>
                                    <div> &#163; {totalPrice} </div>
                                </li>
                            </ul>

                        </div>

                        
                        
                    </div>
                        

                        

                    
			
        
                </main>



                <footer className='footer'>
                    All rights reserved || Developed by Foster Ogwudu
                </footer>

            </div>
        )
    }




export default Placeorderpage;
