import Axios from "axios";
import Cookie from 'js-cookie';
import { USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_REGISTER_FAIL } from '../Constants/Registerconstants';




const register = (name, email, password) => async (dispatch) => {
    

    try {
        dispatch({ type: USER_REGISTER_REQUEST, payload: { name, email, password } })
        const { data } = await Axios.post('http://localhost:3001/api/users/register', { name, email, password });
        dispatch({ type: USER_REGISTER_SUCCESS, payload: data })

        Cookie.get('userInfo', JSON.stringify(data));
    }
    catch (error) {

        dispatch({ type: USER_REGISTER_FAIL, payload: error.message })

    }

}

export  { register };
