import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import { register } from '../../Actions/Registeraction';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { signin } from '../../Actions/Signinaction';
import { useDispatch, useSelector } from 'react-redux';
import { saveProduct, ListProduct, deleteProduct } from '../../Actions/Productaction';









function Productpage (props) {

    const dispatch = useDispatch();
    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const [price, setPrice] = useState('');
    const [brand, setBrand] = useState('');
    const [categories, setCategories] = useState('');
    const [date, setDate] = useState('');
    const [image, setImage] = useState('');
    const [countinstock, setCountinstock] = useState('');
   
    const [description, setDescription] = useState('');
    
    const productSave = useSelector(state => state.productSave);
    const { loading: loadingSave, success: successSave, error: errorSave } = productSave;

    const productDelete = useSelector(state => state.productDelete);
    const { loading: loadingDelete, success: successDelete, error: errorDelete } = productDelete;


    const productList = useSelector(state => state.productList);
    const { loading, products, error } = productList;
    const [modal, setModal] = useState(false);


    
    

    useEffect(() => {
        if (successSave) {
            setModal(false);
        }
        dispatch(ListProduct())
            return () => {
                //
            };

    }, [successSave, successDelete]);
    const openmodal = (product) => {
        setModal(true);

        setId(product._id);
        setName(product.name);
        setPrice(product.price);
        setCategories(product.categories);
        setBrand(product.brand);
        setCountinstock(product.countinstock);
        setImage(product.image);
        setDescription(product.description);
        setDate(product.date);
    }

    const createaccountHandler = (e) => {
        e.preventDefault();

        dispatch(saveProduct({_id : id, name, brand, price, categories, date, image, countinstock, description }));
        
    }

    const deleteHandler = (product) => {
        dispatch(deleteProduct(product._id));
    }


    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='content content-margined'>
                    <div className='product-header'>
                        <h3> Products </h3>
                        <button onClick={() => openmodal({}) }> Create Product </button>
                    </div>

                    {modal && <div className='form'>



                        <form onSubmit={createaccountHandler}>

                            <ul className='form-container'>
                                <li>
                                    <h2> Create Product </h2>
                                </li>
                                <li>
                                    {loadingSave && <div> Loading... </div>}
                                    {errorSave && <div className='error'> {errorSave} </div>}

                                </li>

                                <li>
                                    <label htmlFor='name'> Name </label>
                                    <input type='text' name='name' id='name' value={name} onChange={(event) => { setName(event.target.value) }} />
                                </li>

                                <li>
                                    <label htmlFor='price'> Price </label>
                                    <input type='text' name='price' id='price' value={price} onChange={(event) => { setPrice(event.target.value) }} />
                                </li>
                                <li>
                                    <label htmlFor='brand'> Brand </label>
                                    <input type='text' name='brand' id='brand' value={brand} onChange={(event) => { setBrand(event.target.value) }} />
                                </li>
                                <li>
                                    <label htmlFor='categories'> Categories </label>
                                    <input type='text' name='categories' id='categories' value={categories} onChange={(event) => { setCategories(event.target.value) }} />
                                </li>
                                <li>
                                    <label htmlFor='date'> Date </label>
                                    <input type='date' name='date' id='date' value={date} onChange={(event) => { setDate(event.target.value) }} />
                                </li>
                                <li>
                                    <label htmlFor='image'> Image </label>
                                    <input type='text' name='image' id='image' value={image} onChange={(event) => { setImage(event.target.value) }} />
                                </li>
                                <li>
                                    <label htmlFor='countinstock'> Count in stock </label>
                                    <input type='text' name='countinstock' id='countinstock' value={countinstock} onChange={(event) => { setCountinstock(event.target.value) }} />
                                </li>
                                <li>
                                    <label htmlFor='description'> Description </label>
                                    <textarea name='description' id='description' value={description} onChange={(event) => { setDescription(event.target.value) }} />
                                </li>

                                <li>
                                    <button className='button primary' type='submit'> {id? "Update": "Create" }  </button>
                                </li>


                                <li>
                                    <button className='button primary' onClick={() => { setModal(false) }}> Go back </button>
                                </li>

                            </ul>


                        </form>
                    </div>}

                  

                        <div className='product-list'>

                        <table>
                            <thead>
                                <tr>

                                <th> ID </th>
                                <th> Name </th>
                                <th> Price </th>
                                <th> Brand </th>
                                <th> Category </th>
                                <th> Date </th>
                                <th> Description </th>
                                <th> Description </th>
                                <th> Action </th>

                                </tr>
                            </thead>
                            
                            <tbody>
                                {products.map(product => (
                                    <tr key={product._id}>
                                        <td>{product._id} </td>
                                        <td>{product.name} </td>
                                        <td>{product.price} </td>
                                        <td>{product.brand} </td>
                                        <td>{product.categories} </td>
                                        <td>{product.date} </td>
                                        <td>{product.description} </td>
                                        <td>{product.countinstock} </td>
                                        <td>
                                            <button className='button' onClick={() => openmodal(product)}> Edit </button>
                                            {''}
