import { createStore, combineReducers, applyMiddleware, compose  } from 'redux';
import { productListReducer, productDetailsReducer } from './Reducers/ProductReducer';
import thunk from 'redux-thunk';
import { createLogger } from 'redux-logger'
import { cartReducer } from './Reducers/CartReducer';
import { userSigninReducer } from './Reducers/SigninReducer';
import Cookie from 'js-cookie';
    
/*Cookie helps in preserving the state when screen refreshed is perform */
/* To get access to the cookie and convert it back to JSON object so it can be set as initial state for state cart . Note that || [] sets empty array if there is no item*/
const cartItems = Cookie.getJSON("cartItems") || [];
const userInfo = Cookie.getJSON("userInfo") || '';


const initialState = { cart: { cartItems }, userSignin: {userInfo } };
const logger = createLogger();
const rootreducer = combineReducers({
    productList: productListReducer,
    productDetails: productDetailsReducer,
    cart: cartReducer,
    userSignin: userSigninReducer,
})

const composeEnhancer = window._REDUX_DEVTOOLS_EXTENSION_COMPOSE_ || compose;
const store = createStore(rootreducer, initialState, composeEnhancer(applyMiddleware(thunk, logger)))


export default store
