import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import { Line } from 'react-chartjs-2';
import '../Css/Componentscss.css';
import { useState } from 'react';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Cookies from 'js-cookie';



const PlotScreen = () => {


    /* Getting state of the carbon intensity from the redux store*/
    const carbonIntensityData = useSelector(state => state.carbon);
    const { loading, carbonData } = carbonIntensityData

    /*initialising state for plot data */
    const [plotData, setPlotData] = useState({})

    

    /*function to update plotData state with data and options */
    const chart = () => {
        const forecastData = carbonData.map((data) => { return data.intensity.forecast })
        const actualData = carbonData.map((data) => { return data.intensity.actual })
        setPlotData({
            labels: ['monday', 'tuesday', 'wed', 'Thu', 'fri', 'sat'],
            datasets: [
                {
                    label: 'Forecast Intensity ',

                    data: forecastData,
                    backgroundColor: [
                        'rgba(75, 192, 192, 0.6)'

                    ],
                    bandwidth: 4
                },

                {
                    label: 'Actual Intensity ',
                    data: actualData,
                    backgroundColor: [
                        'rgba(54, 162, 235, 1)'

                    ],
                    bandwidth: 4
                }
            ]


        });


    }
    

    useEffect(() => {
        if (carbonData) {
            chart()
        }
    }, [carbonData])


    return (
        <div>
            <h1> PLOT SCREEN </h1>
            <div>
                <Link to={"/"} > <button className='button' > back </button> </Link>
            </div>
            <div className='plot'>
                
                <Line data={plotData} options={{
                    responsive: true,
                    title: { text: 'THICKNES SCALE', display: true },
                    scales: {
                        yAxes: [
                            {
                                ticks: {
                                    autoSkip: true,
                                    maxTicksLimit: 10,
                                    beginAtZero: true
                                },
                                gridLines: {
                                    display: false
                                }
                            }],
                        xAxes: [{
                            gridLines: {
                                display: false
                            }
                        }]
                    }
                }} />
            </div>
            
        </div>
        )
}

export default PlotScreen;
