import { PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "../Constants/Constants";




export const ListProduct =  (dispatch) => {
    dispatch({ type: PRODUCT_LIST_REQUEST });
    fetch('http://localhost:3001')
        .then(response => response.json())
        .then(data => dispatch({ type: PRODUCT_LIST_SUCCESS, payload: data }))
        .catch(error => dispatch({ type: PRODUCT_LIST_FAIL, payload: error }))
}
