import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import { register } from '../../Actions/Registeraction';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { signin } from '../../Actions/Signinaction';
import { useDispatch, useSelector } from 'react-redux';
import { saveProduct } from '../../Actions/Productaction';









function Productpage (props) {

    const dispatch = useDispatch();
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [brand, setBrand] = useState('');
    const [categories, setCategories] = useState('');
    const [date, setDate] = useState('');
    const [image, setImage] = useState('');
    const [countinstock, setCountinstock] = useState('');
    const [rating, setRating] = useState('');
    const [description, setDescription] = useState('');
    const [numreviews, setNumreviews] = useState('');
    const productSave = useSelector(state => state.productSave)
    const { loading: loadingSave, success: successSave, error: errorSave } = productSave;
    


    
    

    useEffect(() => {

            return () => {
                //
            };
        
    }, []);

    const createaccountHandler = (e) => {
        e.preventDefault();
        dispatch(saveProduct({ name, brand, price, categories, date, image, countinstock, rating, numreviews, description }));
        
    }


    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='form'>

                    <form onSubmit={createaccountHandler}>

                        <ul className='form-container'>
                            <li>
                                <h2> Create Product </h2>
                            </li>
                            <li>
                                {loadingSave && <div> Loading... </div>}
                                {errorSave && <div className='error'> {errorSave} </div>}
                                
                            </li>

                            <li>
                                <label htmlFor='name'> Name </label>
                                <input type='text' name='name' id='name' onChange={(event) => { setName(event.target.value) }} /> 
                            </li>

                            <li>
                                <label htmlFor='price'> Price </label>
                                <input type='text' name='price' id='price' onChange={(event) => { setPrice(event.target.value) }}/> 
                            </li>
                            <li>
                                <label htmlFor='brand'> Brand </label>
                                <input type='text' name='brand' id='brand' onChange={(event) => { setBrand(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='categories'> Categories </label>
                                <input type='text' name='categories' id='categories' onChange={(event) => { setCategories(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='date'> Date </label>
                                <input type='date' name='date' id='date' onChange={(event) => { setDate(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='image'> Image </label>
                                <input type='text' name='image' id='image' onChange={(event) => { setImage(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='countinstock'> Count in stock </label>
                                <input type='text' name='countinstock' id='countinstock' onChange={(event) => { setCountinstock(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='description'> Description </label>
                                <textarea name='description' id='description' onChange={(event) => { setDescription(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='numreviews'> Number of Reviews </label>
                                <input type='text' name='numreviews' id='numreviews' onChange={(event) => { setNumreviews(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='rating'> Rating </label>
                                <input type='text' name='rating' id='rating' onChange={(event) => { setRating(event.target.value) }} />
                            </li>




                            <li>
                                <button className='button primary' type='submit'> Create </button>
                            </li>

                        </ul>


                    </form>



                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default Productpage;
