import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import { register } from '../../Actions/Registeraction';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { signin } from '../../Actions/Signinaction';
import { useDispatch, useSelector } from 'react-redux';









function Signin(props) {

    const dispatch = useDispatch();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const userSignin = useSelector(state => state.userSignin)
    const { loading, userInfo, error } = userSignin;
    const Register = useSelector(state => state.userRegister)
    const { registerInfo } = Register;
    const redirect = props.location.search ? props.location.search.split("=")[1] : '/';



    
    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(signin(email.toLowerCase(), password))
    }

    useEffect(() => {
        if (userInfo) {
            props.history.push(redirect);
        }
        else {
            return () => {

            };
        }
    }, [userInfo]);

    const createaccountHandler = (e) => {
        e.preventDefault();
        dispatch(register());
        props.history.push('/register');
    }


    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='form'>
                    
                    <form onSubmit={submitHandler}>

                        <ul className='form-container'>
                            <li>
                                <h2> Sign-in </h2>
                            </li>
                            <li>
                                {loading && <div> Loading... </div>}
                                {error && <div className='error'> incorrect username or password </div>}
                                
                            </li>

                            <li>
                                <label htmlFor='email'> Email </label>
                                <input type='email' name='email' id='email' onChange={(event) => { setEmail(event.target.value) }}/> 
                            </li>

                            <li>
                                <label htmlFor='password'> Password </label>
                                <input type='password' name='password' id='password' onChange={(event) => { setPassword(event.target.value) }}/> 
                            </li>

                            <li>
                                <button className='button primary' type='submit'> signin </button>
                            </li>

                            <li>
                                New to Fosy Store?
                            </li>
                            <li>
                                <button className='button secondary' onClick={ createaccountHandler}> Create your Fosy Store account </button>
                            </li>

                        </ul>


                    </form>



                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default Signin;
