import axios from 'axios';
import { CART_ADD_ITEM, CART_SAVE_SHIPPING, CART_SAVE_PAYMENT } from '../Constants/Cartconstants';
import { REMOVE_FROM_CART } from '../Constants/Cartconstants';
import Cookie from 'js-cookie';


const addToCart = (productid, qty) => async (dispatch, getState) => {
	try {

		const { data } = await axios.get('http://localhost:3001/api/product/product/' + productid);
		dispatch({
			type: CART_ADD_ITEM, payload: {
				product: data._id,
				name: data.name,
				image: data.image,
				price: data.price,
				countinstock: data.countinstock,
				qty
			}
		})
	
	}
	catch (error){
		
	}
	/*Implementing cookies from preserving state - cart itemms on page refresh*/
	/*define getstate function to use get cart state. The getstate runs once dispatch action is completed; then setting cookies follows suite*/
	const { cart: { cartItems } } = getState();
	/*seeting cookie ('name', 'value') Note that JSON.strigify coverts the state which is in object type to string type*/
	Cookie.set("cartItems", JSON.stringify(cartItems));
}

export { addToCart };

const removeFromCart = (productid) => (dispatch, getState) => {
	try {
		dispatch({
			type: REMOVE_FROM_CART,
			payload: productid
		});
		
	}
	catch (error) {

	}
	/*Implementing cookies from preserving state - cart itemms on page refresh*/
	/*define getstate function to use get cart state. The getstate runs once dispatch action is completed; then setting cookies follows suite*/
	const { cart: { cartItems } } = getState();
	/*seeting cookie ('name', 'value') Note that JSON.strigify coverts the state which is in object type to string type*/
	Cookie.set("cartItems", JSON.stringify(cartItems));
}

export { removeFromCart };



const saveShipping = (data) => (dispatch, getState) => {

	try {
		dispatch({
			type: CART_SAVE_SHIPPING,
			payload: data
		});
	}
	catch{
		
	}

	const { cart: { shipping } } = getState();
	Cookie.set("shipping", JSON.stringify(shipping));
}

export { saveShipping };

const savePayment = (paymentmethod) => (dispatch, getState) => {

	try {
		dispatch({
			type: CART_SAVE_PAYMENT,
			payload: paymentmethod
		});
	}
	catch{

	}
	const { cart: { payment } } = getState();
	Cookie.set("payment", JSON.stringify(payment));
}

export { savePayment };
