import React from 'react';
import ReactDOM from 'react-dom';
import { Link, Route, Switch } from 'react-router-dom';
import './Component/Css/Componentscss.css';

import Frontpage from './Component/Pages/Frontpage';
import Detailspage from './Component/Pages/detailspage';

class App extends React.Component {
    constructor() {

        super();
        this.state = {

            sidebar: 'sidebarclose'
        }

    }




    openmenu = () => {

        this.setState({ sidebar: 'sidebaropen' })

    }


    closemenu = () => {
        this.setState({ sidebar: 'sidebarclose' })
    }



    render() {

        return (
            <div>
                <Route exact={true} path="/" component={Frontpage} />
                <Route exact={true} path="/products/:id" component={Detailspage} />

                <div>

                    <header className='header'>
                        <div className='brand'>
                            <button onClick={this.openmenu}> &#9776; </button>


                        </div>

                        <div className='header-links'>
                            <a href="signin.html"> Signin </a>
                            <a href="Cart.html"> Cart </a>

                        </div>
                    </header>

                    <aside className={this.state.sidebar}>
                        <h3> Shopping Categories </h3>
                        <button className='sidebar-close-button' onClick={this.closemenu}> X </button>
                        <ul>

                            <li>

                                <a href='index.html'> Pants </a>
                            </li>

                            <li>

                                <a href='index.html'> Shirts </a>
                            </li>

                            <li>

                                <a href='index.html'> Suits </a>
                            </li>

                            <li>

                                <a href='index.html'> Trousers </a>
                            </li>




                        </ul>
                    </aside>

                </div>

            </div>
            )
    }


}

export default App;
