class Productdetails extends React.Component {

	constructor(props) {

		super();
		this.state = {
			product: [],
		}

	}



	componentDidMount() {

		fetch('http://localhost:3001')
			.then(response => response.json())
			.then(data => { this.setState({ product: (data.find (x => x.id === this.props.id))  }) });

		
		
	}




	render() {
		 
		return (
			<div className='content'>
				
					

					 

