import ReactDOM from 'react-dom';
import React, { Component, useReducer } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';

import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { register } from '../../Actions/Registeraction';
import { useDispatch, useSelector } from 'react-redux';









function Register(props) {

    const dispatch = useDispatch();
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
   
    const [repassword, setRepassword] = useState('');
    const [passwordErrorMessage, setPasswordErrorMessage] = useState('');
    const [existingUserErrorMessage, setExistingUserErrorMessage] = useState('');
    const userRegister = useSelector(state => state.userSignin)
    const { loading, userInfo, error } = userRegister;
    const Register = useSelector(state => state.userRegister)
    const { registerInfo } = Register;
    const [state] = useReducer(Register, { userdata: registerInfo });
    
    const submitHandler = (e) => {
        e.preventDefault();
        if (password === repassword) {
            dispatch(register(name, email, password));
            
            
        }
        else {

            setPasswordErrorMessage('Sorry! confirm password does not match with password')
        }
    }

    useEffect(() => {
        
        checkValid()
    })

    function checkValid() {
        console.log(state.email);
        console.log(state);
        

        if (state.email === '') {
            console.log(state.email);
            console.log(state);
        }

        else {
            if (state.email === 'false' ) {
                console.log(state.email);
                
                alert('Sorry! email is already in use')

            }
            else {
                if (state.email ===  email ) {
                    console.log(state.email);
                    console.log(state);
                    alert('Success! your account has been registered. You can now sign in');
                    props.history.push('/signin');
                }
            }
        }
    }

    
    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='form'>
                    
                    <form onSubmit={submitHandler}>

                        <ul className='form-container'>
                            <li>
                                <h2> Register </h2>
                            </li>
                            <li>
                               
                                {loading && <div> Loading... </div>}
                                {error && <div className='error'> {error} </div>}
                                {existingUserErrorMessage && <div className='error'> {existingUserErrorMessage} </div>}
                                

                            </li>

                            <li>
                                <label htmlFor='name'> Name </label>
                                <input required='true' type='text' name='name' id='name' onChange={(event) => { setName(event.target.value) }} />
                            </li>


                            <li>
                                <label htmlFor='email'> Email </label>
                                <input required='true' type='email' name='email' id='email' onChange={(event) => { setEmail(event.target.value) }}/> 
                            </li>

                            <li>
                                <label htmlFor='password'> Password </label>
                                <input required='true' type='password' name='password' id='password' onChange={(event) => { setPassword(event.target.value) }} /> 
                            </li>

                            <li> {passwordErrorMessage && <div className='error'> {passwordErrorMessage} </div>}

                            </li>
                            <li>
                                <label htmlFor='repassword'> Re-Enter Password </label>
                                <input required='true' type='password' name='repassword' id='repassword' onChange={(event) => { setRepassword(event.target.value) }} />
                            </li>


                            <li>
                                <button className='button primary' type='submit'> Register </button>
                            </li>

                            <li>
                                Already have an account? <Link to="/signin"> Sign-in </Link>
                            </li>
                            
                        </ul>


                    </form>



                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default Register;
