import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import './Frontpage.css';
import Slim1 from '../Asset/slim1.jpg';
import Product from './Product'



class ProductList extends React.Component {

	constructor() {

		super();
		this.state = {
			product: [],
		}

	}



	componentDidMount() {

		fetch('http://localhost:3001')
			.then(response => response.json())
			.then(data => { this.setState({ product: data || [] }) });

		console.log(this.state.product);
		
	}




	render() {
		 
		return (
			<div className='content'>
				<ul className='products'>
					<li>

						<div className='product'>
							<img className='product-image' src={Slim1} alt='Slim1' />
							<div className='product-name'> <a href='product.html'>  Slim Shirt </a> </div>
							<div className='product-brand'> Nike </div>
							<div className='product-price'> &#163;100 </div>
							<div className='product-rating'> 4.5 starts (20 reviews) </div>


						</div>

					</li>





				</ul>

			</div>
			)
	}


}

export default ProductList;
