import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import ProductList from '../Product/Productlist.js';
import { Link, Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';




const mapStateToProps = state => {
    return {
        userInfo: state.userSignin.userInfo,
        
    }
}




class Frontpage extends React.Component {

    constructor() {

        super();
        this.state = {
            
            sidebar: 'sidebarclose'
        }

    }




    openmenu = () => {
        
            this.setState({ sidebar: 'sidebaropen' })

    }


    closemenu = () => {
        this.setState({ sidebar: 'sidebarclose' })
    }

    render() {
        const { userInfo } = this.props; 
        return (
            <div className='Frontpage grid-container'>
                <header className='header header-links'>
                    <div className='brand'>
                        <button onClick={this.openmenu}> &#9776; </button>
                        <Link className='brand' to={"/"}> Fosy Store </Link>
                         
                    </div>

                    <div className='header-links'>
                        {userInfo ? <link to='/profile'> {userInfo.name} </link> :
                            <Link to="/signin"> Signin </Link>
                            }
                        
                        <a href="Cart.html"> Cart </a>

                    </div>
                </header>

                <aside className={this.state.sidebar}>
                    <h3> Shopping Categories </h3>
                    <button className='sidebar-close-button' onClick={this.closemenu}> X </button>
                    <ul>

                        <li>

                            <a href='index.html'> Pants </a>
                        </li>

                        <li>

                            <a href='index.html'> Shirts </a>
                        </li>

                        <li>

                            <a href='index.html'> Suits </a>
                        </li>

                        <li>

                            <a href='index.html'> Trousers </a>
                        </li>




                    </ul>
                </aside>

               
            </div>
            )
    }


}

export default connect(mapStateToProps) (Frontpage);
