import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import { connect } from 'react-redux';
import { ProductDetails } from '../../Actions/Productaction';
import { productListReducer } from '../../Reducers/ProductReducer';

import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';





const mapStateToProps = state => {
    return {
        product: state.productDetails.product,
        loading: state.productDetails.loading
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onproductdetailrequest: (event) => dispatch(ProductDetails(event))
    }
}




class Detailspage extends React.Component {

    constructor() {

        super();
        this.state = {
            Qty : 1
        }

    }

    


    componentDidMount() {
        this.props.onproductdetailrequest(this.props.match.params.id)



    } 


    OnsetQty = (event) => {
        this.setState({ Qty: event.target.value });
        
    }


    render() {
        
        const { product, loading } = this.props;
        return  (
            <div className='Frontpage grid-container'>
                <Navbar />

                <main className='main'>
                    <div >

                        <div className='backtoresult'>
                            <Link to='/' > Back to result </Link>
                        </div>
                        {loading ? <div> Loading ... </div> : <div className='details'>
                            <div className='details-image'>
                                <img src={product.image} alt='product' width='500rem' />
                            </div>
                            <div className='details-info'>
                                <ul>
                                    <li>
                                        <h4> {product.name} </h4>
                                    </li>

                                    <li>
                                        4 stars
                                    </li>

                                    <li>
                                        <b> {product.brand} </b>
                                    </li>
                                    <li>
                                        <b> product description </b>
                                    </li>

                                    <li>
                                        <b> Price : &#163;{product.price} </b>
                                    </li>


                                </ul>


                            </div>

                            <div className='details-action'>
                                <ul>
                                    <li>
                                        <b> Price : &#163;{product.price} </b>
                                    </li>

                                    <li>
                                        <b>Status : </b>
                                    </li>

                                    <li>
                                        <b> Qty: <select value={this.state.Qty} onChange={this.OnsetQty}>
                                            {[...Array(product.countinstock).keys()].map(x =>
                                                <option key={x + 1} value={x + 1}> {x + 1} </option>
                                                )}
                                        </select>

                                        </b>
                                    </li>

                                    <li>
                                        <button className='button'> Add to Cart </button>
                                    </li>



                                </ul>
                            </div>
                        </div>
 }
                        
                        </div>
                        

                        

                    
			
        
                </main>



                <footer className='footer'>
                    All rights reserved || Developed by Foster Ogwudu
                </footer>

            </div>
        )
    }


}

export default connect(mapStateToProps, mapDispatchToProps)(Detailspage);
