import ReactDOM from 'react-dom';
import React, { Component, useReducer } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';

import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { register } from '../../Actions/Registeraction';
import { useDispatch, useSelector } from 'react-redux';
const redirect = props.location.search ? props.location.search.split("=")[1] : '/';









function Register(props) {

    const dispatch = useDispatch();
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
   
    const [repassword, setRepassword] = useState('');
    const [passwordErrorMessage, setPasswordErrorMessage] = useState('');
    const [existingUserErrorMessage, setExistingUserErrorMessage] = useState('');
    const userRegister = useSelector(state => state.userSignin)
    const { loading, userInfo, error } = userRegister;
    const Register = useSelector(state => state.userRegister)
    const { registerInfo } = Register;
    
    
    const submitHandler = (e) => {
        e.preventDefault();
        if (password === repassword) {
            setPasswordErrorMessage('')
            dispatch(register(name, email.toLowerCase(), password));

        }
        else {

            setPasswordErrorMessage('Sorry! confirm password does not match with password')
        }
    }

    useEffect(() => {
        
        if (registerInfo === 'new user') {
            alert('Account created')
            props.history.push(redirect);
        }


    })

    const validChecker = (event) => {
        // Validate lowercase letters
        const lowerCaseLetters = /[a-z]/g;
        if (event.target.value.match(lowerCaseLetters)) {
            document.getElementById("letter").classList.remove("invalid");
            document.getElementById("letter").classList.add("valid");
        } else {
            document.getElementById("letter").classList.remove("valid");
            document.getElementById("letter").classList.add("invalid");
        }

        // Validate capital letters
        var upperCaseLetters = /[A-Z]/g;
        if (event.target.value.match(upperCaseLetters)) {
            document.getElementById("capital").classList.remove("invalid");
            document.getElementById("capital").classList.add("valid");
        } else {
            document.getElementById("capital").classList.remove("valid");
            document.getElementById("capital").classList.add("invalid");
        }

        // Validate numbers
        var numbers = /[0-9]/g;
        if (event.target.value.match(numbers)) {
            document.getElementById("number").classList.remove("invalid");
            document.getElementById("number").classList.add("valid");
        } else {
            document.getElementById("number").classList.remove("valid");
            document.getElementById("number").classList.add("invalid");
        }

        // Validate length
        if (event.target.value.length >= 8) {
            document.getElementById("length").classList.remove("invalid");
            document.getElementById("length").classList.add("valid");
        } else {
            document.getElementById("length").classList.remove("valid");
            document.getElementById("length").classList.add("invalid");
        }
    }

    
    
    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='form'>
                    
                    <form onSubmit={submitHandler}>

                        <ul className='form-container'>
                            <li>
                                <h2> Register </h2>
                            </li>
                            <li>
                               
                                {loading && <div> Loading... </div>}
                                {error && <div className='error'> {error} </div>}
                                {registerInfo === false && <div className='error'> Sorry, this email has been registered before </div>}
                                

                            </li>

                            <li>
                                <label htmlFor='name'> Name </label>
                                <input required='true' type='text' name='name' id='name' onChange={(event) => { setName(event.target.value) }} />
                            </li>


                            <li>
                                <label htmlFor='email'> Email </label>
                                <input required='true' type='email' name='email' id='email' onChange={(event) => { setEmail((event.target.value).toLocaleLowerCase()) }} /> 
                            </li>

                            <li>
                                <label htmlFor='password'> Password </label>
                                <input required='true' type='password' name='password' id='password' onChange={(event) => { setPassword(event.target.value) }}
                                    pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}" onFocus={function () {
                                        document.getElementById("message").style.display = "block";
                                    }} onBlur={function () {
                                        document.getElementById("message").style.display = "none";
                                    }} onKeyUp={validChecker} /> 
                            </li>
                            

                            <div id="message">
                                
                                    <h3>Password must contain the following:</h3>
                                    <p id="letter" class="invalid">A <b>lowercase</b> letter</p>
                                    <p id="capital" class="invalid">A <b>capital (uppercase)</b> letter</p>
                                    <p id="number" class="invalid">A <b>number</b></p>
                                    <p id="length" class="invalid">Minimum <b>8 characters</b></p>
                                
                                </div>
                            
                            <li> {passwordErrorMessage && <div className='error'> {passwordErrorMessage} </div>}

                            </li>
                            <li>
                                <label htmlFor='repassword'> Confirm Password </label>
                                <input required='true' type='password' name='repassword' id='repassword' onChange={(event) => { setRepassword(event.target.value) }} />
                            </li>


                            <li>
                                <button className='button primary' type='submit'> Register </button>
                            </li>

                            <li>
                                Already have an account?  <Link to={redirect === "/" ? "signin" : "signin?redirect" + redirect} className='button secondary' onClick={createaccountHandler}> Create your Fosy Store account </Link>
                            </li>
                            
                        </ul>


                    </form>



                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default Register;
