import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/cartpageonly.css';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { addToCart, removeFromCart } from '../../Actions/Cartaction';
import { useDispatch, useSelector } from 'react-redux';









function Cartpage  (props)  {

    const cart = useSelector(state => state.cart); /* access the state in the cart state inside store generated from cart reducer*/
    const { cartItems } = cart; /* declaring cartitems as cart in the above step*/
    const productid = props.match.params.id;
    const qty = props.location.search ? Number(props.location.search.split("=")[1]) : 1;
    const dispatch = useDispatch();


    const removeFromCartHandler = (productid) => {
        dispatch(removeFromCart(productid));

    }

    const checkoutHandler = () => {
        props.history.push('/signin?redirect=shipping')
    }

    useEffect(() => {
        if (productid) {
            dispatch(addToCart(productid, qty)); 
        }

    }, [])


        
        return  (
            <div className='Frontpage grid-container'>
                

                <main className='main'>
                    <div className='cart' >

                        <div className='cart-list'>
                            <ul className='cart-list-container'>

                                <li>
                                    <h3> Shopping Cart </h3>
                                    <div> Price </div>

                                </li>

                                {cartItems.length === 0 ?
                                    <div> Cart is empty </div> :
                                    cartItems.map(item =>
                                        <div className='cart-list-container-item'>
                                            <div className='cart-image'>  
                                                <img src={item.image} alt='product' />
                                            </div>
                                            <div className='cart-name'>
                                                <div>
                                                    <Link className='cart-name-link' to={"/products/" + item.product}> 
                                                        {item.name}
                                                    </Link>
                                                </div>
                                                <div>
                                                    Qty:
                                                    <select value={item.qty} onChange={(event) => dispatch(addToCart (item.product, event.target.value))}>
                                                        {Array.from(Array(item.countinstock).keys()).map(x =>
                                                            <option key={x + 1} value={x + 1}> {x + 1} </option>
                                                        )}
                                                    </select>
                                                    <button type='button' className='button primary' onClick={() => removeFromCartHandler(item.product)}> Delete </button>
                                                </div>

                                            </div>

                                            <div className='cart-price'>
                                               &#163;{item.price}

                                            </div>

                                        </div>
                                        )
                                    }

                            </ul>

                        </div>

                        <div className='cart-action'>
                            <h3>
                                Subtotal ({cartItems.reduce((a, c) => a + c.qty, 0)} items)
                                :
                            &#163; {cartItems.reduce((a, c) => a + c.price * c.qty, 0)}

                            </h3>

                            <button className='button full-width' disabled={cartItems.length === 0} onClick={checkoutHandler}>

                                Proceed to Checkout
                            </button>
                        </div>

                        
                        
                    </div>
                        

                        

                    
			
        
                </main>



                <footer className='footer'>
                    All rights reserved || Developed by Foster Ogwudu
                </footer>

            </div>
        )
    }




export default Cartpage;
