import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import {getDataAction } from '../../Actions/GetDataAction.js';
import { useDispatch, useSelector } from 'react-redux';













function CarbonScreen (props) {

    const dispatch = useDispatch();
    /*setting state to store selected date*/ 
    const [dateSelected, setDateSelected] = useState();

    /* Getting state of the carbon intensity from the redux store*/
    const carbonIntensityData = useSelector(state => state.carbon);
    const { loading, carbonData } = carbonIntensityData
    

    /*getting today date*/
    const date = new Date();
    

    /*getting the date of the last first day*/
    let lastFirstDay = new Date(date)
    lastFirstDay.setDate(lastFirstDay.getDate() - 1)

    /*getting the date of the last second day*/
    let lastSecondDay = new Date(date)
    lastSecondDay.setDate(lastSecondDay.getDate() - 2)

    /*getting the date of the last third day*/
    let lastThirdDay = new Date(date)
    lastThirdDay.setDate(lastThirdDay.getDate() - 3)

    /*getting the date of the last fourth day*/
    let lastFourthDay = new Date(date)
    lastFourthDay.setDate(lastFourthDay.getDate() - 4)

    /*getting the date of the last fifth day*/
    let lastFifthDay = new Date(date)
    lastFifthDay.setDate(lastFifthDay.getDate() - 5)




    /*handling the selected date button click and updating the dateSelected state in the right format for Carbon Intensity API*/
    const dateSelectedHandler = (e) => {
        
        let day = ("0" + e.getDate()).slice(-2);
        let month = ("0" + (e.getMonth() + 1
        )).slice(-2);
        let year = e.getFullYear();
        let date = (year + "-" + month + "-" + day)
        setDateSelected(date);
        

        
        
    }
/*dispatching action for fetching carbon intensity data if date is selected and redirecting to plot screen*/
    const callFn = () => {
        dispatch(getDataAction(dateSelected));
        
    }
    const setcookies = () => {

    }

    /*calling the function that will dispatch action to get data from the Carbon Intensity API whenever dateSelected state is updated*/
    useEffect(() => {
        if (dateSelected) {
            console.log(dateSelected)
            

            carbonData.map((data) => { console.log('hiiyoi' + data.from) })

            callFn()
            props.history.push('/plot')
        }
    }, [dateSelected]);

  

    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='content content-margined'>
                   

                  

                    <div className='product-list'>
                        <table className='table'>
                            <thead>
                                <tr>

                                <th> SELECT A DATE </th>
                               
                                                                
                                </tr>
                            </thead>
                            
                            <tbody>
                                
                                <tr>
                                    <td><button className='button full-width' onClick={(event) => { dateSelectedHandler(lastFirstDay) }}> {lastFirstDay.toDateString()} </button> </td>
                                        
                                   </tr>

                                <tr>
                                    <td> <button className='button full-width' onClick={(event) => { dateSelectedHandler(lastSecondDay) }}> {lastSecondDay.toDateString()} </button> </td>

                                </tr>

                                <tr>
                                    <td> <button className='button full-width' onClick={(event) => { dateSelectedHandler(lastThirdDay) }} > {lastThirdDay.toDateString()} </button> </td>
                                </tr>

                                <tr>
                                    <td> <button className='button full-width' onClick={(event) => { dateSelectedHandler(lastFourthDay) }} > {lastFourthDay.toDateString()} </button> </td>
                                </tr>

                                <tr>
                                    <td> <button className='button full-width' onClick={(event) => { dateSelectedHandler(lastFifthDay) }} > {lastFifthDay.toDateString()} </button> </td>

                                </tr>
                                
                                
                            </tbody>
                        </table>
                    </div>

                




                </div>


               

            </main>



            <footer className='footer'>
                All rights reserved 
                </footer>

        </div>
    )
}




export default CarbonScreen;
