import React, { Component } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import { register } from '../../Actions/Registeraction';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';
import { signin } from '../../Actions/Signinaction';
import { useDispatch, useSelector } from 'react-redux';
import { saveProduct, ListProduct, deleteProduct } from '../../Actions/Productaction';









function CarbonScreen (props) {

    const dispatch = useDispatch();
    const [indexy, setIndex] = useState('');

    /*getting today date*/
    let date = new Date();
    let day = ("0" + date.getDate()).slice(-2);
    let month = ("0" + (date.getMonth() + 1
    )).slice(-2);
    let year = date.getFullYear();
    let today = (year + "-" + month + "-" + day)

/*getting the date of the last five days*/
    let firstDay = day + 1;
    let lastFirstDay = (year + "-" + month + "-" + firstDay)
    
    return (
        <div className='Frontpage grid-container'>
            

            <main className='main'>
                <div className='content content-margined'>
                   

                  

                        <div className='product-list'>

                        <table className='table'>
                            <thead>
                                <tr>

                                <th> DATE </th>
                                <th> Forecast Intensity </th>
                                <th> Actual intensity </th>
                                <th> Index </th>
                                                                
                                </tr>
                            </thead>
                            
                            <tbody>
                                
                                <tr>
                                    <td>{lastFirstDay} </td>
                                        <td>'Tuesday' </td>
                                        <td>'TWednesday' </td>
                                        <td>'Tuesday' </td>                                                                         
                                      
                                   </tr>

                                <tr>
                                    <td>{today} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>

                                <tr>
                                    <td>{new Date().toLocaleString()} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>

                                <tr>
                                    <td>{new Date().toLocaleString()} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>

                                <tr>
                                    <td>{new Date().toLocaleString()} </td>
                                    <td>'Tuesday' </td>
                                    <td>'TWednesday' </td>
                                    <td>'Tuesday' </td>

                                </tr>
                                
                                
                            </tbody>
                        </table>
                    </div>

                




                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default CarbonScreen;
