import ReactDOM from 'react-dom';
import React, { Component, useReducer } from 'react';
import '../Css/Componentscss.css';
import '../Css/signinpagecss.css';
import countryList from 'react-select-country-list';
import { useState, useEffect, history } from 'react';
import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';

import { useDispatch, useSelector } from 'react-redux';
import { saveShipping } from '../../Actions/Cartaction';
import Checkoutsteps from '../Checkoutsteps/Checkoutsteps';










function Shippingpage(props) {

    const dispatch = useDispatch();
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('');
    const [city, setCity] = useState('');

    const [postalcode, setPostalcode] = useState('');
    const [country, setCountry] = useState('');
    const countries = countryList().getData();
   


    const submitHandler = (e) => {
        e.preventDefault();

        dispatch(saveShipping(address, city, postalcode, country));
        console.log(countries)
        
    }

    



    return (
       
        <div className='Frontpage grid-container'>
            <div>
                <Checkoutsteps step1 step2 >  </Checkoutsteps>
            </div>


            <main className='main'>
                <div className='form'>

                    <form onSubmit={submitHandler}>

                        <ul className='form-container'>
                            <li>
                                <h2> Shipping </h2>
                            </li>
                           
                            <li>
                                <label htmlFor='address'> Address </label>
                                <input required='true' type='text' name='address' id='address' onChange={(event) => { setAddress(event.target.value) }} />
                            </li>
                            
                            <li>
                                <label htmlFor='city'> City </label>
                                <input required='true' type='text' name='city' id='city' onChange={(event) => { setCity(event.target.value) }} />
                            </li>
                            <li>
                                <label htmlFor='postalcode'> Postal Code </label>
                                <input required='true' type='text' name='postalcode' id='postalcode' onChange={(event) => { setPostalcode(event.target.value) }} />
                            </li>
                            
                            <li>
                                <label htmlFor='country'> Country </label>
                                <select>
                                    {countries.map(country => < option key={country.label}> {country.label}  </option>)}
                                </select>
                            </li>


                            <li>
                                <button className='button primary' type='submit'> Continue </button>
                            </li>

                        </ul>


                    </form>



                </div>






            </main>



            <footer className='footer'>
                All rights reserved || Developed by Foster Ogwudu
                </footer>

        </div>
    )
}




export default Shippingpage;
