import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import '../Css/Componentscss.css';
import { connect } from 'react-redux';
import { ProductDetails } from '../../Actions/Productaction';
import { productListReducer } from '../../Reducers/ProductReducer';

import { Link, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from '../Navbar/Navbar.js';





const mapStateToProps = state => {
    return {
        products: state.productList.products,
        loading: state.productList.loading
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onproductdetailrequest: () => dispatch(ProductDetails(productid))
    }
}




class Detailspage extends React.Component {
    
    constructor(props) {
        
        super();
        
        this.state = {
            product:[],
            id: props.match.params.id,
        }

    }

    

    componentDidMount() {
        this.setState({ id: this.props.match.params.id });
        fetch('http://localhost:3001/product/'+ this.state.id)
            .then(response => response.json())
            .then(data => { this.setState({ product: data }) });



    }
    
    render() {
        let person = this.props.match.params.id;
        const { products, loading } = this.props;
        return (
            <div className='Frontpage grid-container'>
                <Navbar />

                <main className='main'>
                    <div >

                        <div className='backtoresult'>
                            <Link to='/' > Back to result </Link>
                        </div>

                        <div className='details'>
                            <div className='details-image'>
                                <img src={this.state.product.image} alt='product' width='500rem' /> 
                            </div>
                            <div className='details-info'>
                                <ul>
                                    <li>
                                        <h4> {this.state.product.name} </h4>
                                    </li>

                                    <li>
                                         4 stars 
                                    </li>

                                    <li>
                                        <b> {this.state.product.brand} </b>
                                    </li>
                                    <li>
                                        <b> product description </b>
                                    </li>

                                    <li>
                                        <b> Price : &#163;{this.state.product.price} </b>
                                    </li>
                                    
                                                                                                         
                                </ul>


                            </div>

                            <div className='details-action'>
                                <ul>
                                    <li>
                                        <b> Price : &#163;{this.state.product.price} </b>
                                    </li>

                                    <li>
                                        <b>Status : </b>
                                    </li>

                                    <li>
                                       <b> Qty: <select>
                                            <option>1 </option>
                                            <option>2 </option>
                                            <option>3 </option>
                                            <option>4 </option>
                                            <option>5 </option>
                                        </select>

                                        </b>
                                    </li>

                                    <li>
                                        <button className='button'> Add to Cart </button>
                                    </li>
                                    

                                    
                                </ul>
                            </div>
                        </div>

                        </div>
                        

                        

                    
			
        
                </main>



                <footer className='footer'>
                    All rights reserved || Developed by Foster Ogwudu
                </footer>

            </div>
        )
    }


}

export default connect(mapStateToProps, mapDispatchToProps)(Detailspage);
